import React from 'react';
import { Button } from '@mantine/core';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { Title } from '@mantine/core';
import illustration from '../assets/illustration.svg';

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin: 10rem 0;
`;
const Hero = styled.div`
  display: flex;
`;
const Gradient = styled.span`
  background: linear-gradient(-70deg, #73ffbb 0%, #64acff 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`;

const Home = () => {
  const navigate = useNavigate();
  const handleNavigate = () => {
    navigate('/questions');
  };
  return (
    <>
      <Hero>
        <Wrapper>
          <Title order={1} style={{ fontSize: 42 }}>
            What's the best <Gradient>tech stack</Gradient> for your
            application?
          </Title>
          <p>
            Answer some questions so that we can offer recommendations that suit
            your application's needs
          </p>
          <Button
            color="cyan"
            radius="md"
            size="md"
            uppercase
            onClick={handleNavigate}>
            Start
          </Button>
        </Wrapper>
        <img src={illustration} alt="web illustration" />
      </Hero>
      <a href="https://storyset.com/web">Web illustrations by Storyset</a>
    </>
  );
};

export default Home;
